import rclpy
from rclpy.node import Node
from unitree_go.msg import SportModeState
from tf2_ros import TransformBroadcaster
from geometry_msgs.msg import TransformStamped, Quaternion  # 从geometry_msgs导入Quaternion
import tf_transformations  # 用于RPY转四元数

class TFDynamicBroadcaster(Node):
    def __init__(self):
        super().__init__('tf_dynamic_broadcaster')
        self.sub_ = self.create_subscription(
            SportModeState,
            '/lf/sportmodestate',
            self.state_cb,
            10
        )
        
        self.timer_ = self.create_timer(0.1, self.timer_cb)
        self.tf_broadcaster_ = TransformBroadcaster(self)
        self.body_height_ = 0.0
        
        self.get_logger().info('启动动态tf变换节点')

    def state_cb(self, state_msg):
        self.body_height_ = state_msg.body_height + 0.057 ## 0.057: base_link在趴着的情况下的高度

    def timer_cb(self):
        transform = TransformStamped()
        transform.header.stamp = self.get_clock().now().to_msg()
        transform.header.frame_id = 'base_footprint'
        transform.child_frame_id = 'base_link'
        
        transform.transform.translation.x = 0.0
        transform.transform.translation.y = 0.0
        transform.transform.translation.z = self.body_height_
        
        quat = tf_transformations.quaternion_from_euler(0.0, 0.0, 0.0)  # (roll, pitch, yaw)
        transform.transform.rotation = Quaternion(x=quat[0], y=quat[1], z=quat[2], w=quat[3])
        
        self.tf_broadcaster_.sendTransform(transform)

def main(args=None):
    rclpy.init(args=args)
    node = TFDynamicBroadcaster()
    rclpy.spin(node)
    node.destroy_node()
    rclpy.shutdown()

if __name__ == '__main__':
    main()